import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { FormArray, FormControl, FormControlName, FormGroup, Validators } from '@angular/forms';
import { DataService } from '../data.service';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.scss']
})
export class UserFormComponent implements OnInit{
  @Output() sendData = new EventEmitter<any>();
  userDataForm: FormGroup;
  submitted = false;
  qualification = '';

  constructor(private data : DataService, private router ){}

  answer = {
    name: '',
    dob:'',
    email:'',
    number:'',
    schoolName:'',
    qualification:'',
    percentage:'',
    hobby:[],
    gender:'',
    address:'',
    summery:'',
  }

  ngOnInit(): void {
     this.userDataForm = new FormGroup({
      'name' : new FormControl(null, Validators.required),
      'dob' : new FormControl(null, Validators.required),
      'email' : new FormControl(null, [Validators.required,Validators.email]),
      'number' : new FormControl(null, Validators.required),
      'schoolName': new FormControl(null, Validators.required),
      'qualification': new FormControl(null, Validators.required),
      'percentage': new FormControl(null, Validators.required),
       'hobby': new FormGroup({
        'cricket' : new FormControl(null),
        'travelling' : new FormControl(null),
        'music' : new FormControl(null),
        'finance' : new FormControl(null),
        'others' : new FormControl(null),
      }),
      'gender' : new FormControl(null, Validators.required),
      'address' : new FormControl(null),
      'summery' : new FormControl(null)
     })
  }

  changeQua(e:any) {
    console.log(e.target.value);
    this.qualification = e.target.value
     console.log(this.userDataForm.value.hobby);
    // console.log(this.qualification);
    // this.qualification.setValue(e.target.value)
  }

  onSubmit() {
    console.log(this.userDataForm.value);
    this.submitted= true
    this.answer.name = this.userDataForm.value.name
    this.answer.dob = this.userDataForm.value.dob
    this.answer.email = this.userDataForm.value.email
    this.answer.number = this.userDataForm.value.number
    this.answer.schoolName = this.userDataForm.value.schoolName
    this.answer.qualification = this.userDataForm.value.qualification
    this.answer.percentage = this.userDataForm.value.percentage
    this.answer.hobby = this.userDataForm.value.hobby
    this.answer.gender = this.userDataForm.value.gender
    this.answer.address = this.userDataForm.value.address
    this.answer.summery = this.userDataForm.value.summery

    console.log(this.userDataForm.value.hobby);
    this.data.dataemit(this.userDataForm.value)
     this.router.navigate(['/details'])

  }



}
