import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { DataService } from '../data.service';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-user-data',
  templateUrl: './user-data.component.html',
  styleUrls: ['./user-data.component.scss'],
})
export class UserDataComponent implements OnInit {
  @Output() editData = new EventEmitter<Object>();
  sharedData: any;
  editMode = false;
  usersDataArray: any[] = [];

  constructor(
    private dataService: DataService,
    private router: Router,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.sharedData = this.dataService.getSharedData();
    // console.log('anwer');
    // console.log(this.sharedData);
    // console.log(this.sharedData.hobby);
    this.dataService.getData().subscribe((response: any) => {
      this.usersDataArray = response;
      console.log('Get call Success');
      console.log(this.usersDataArray);
    });
  }

  onEdit(data: any) {
    this.dataService.editMode = true;
    console.log(this.dataService.editMode);
    this.router.navigate(['/editDetails']);
  }

  getKeysWithTrueValues(obj: { [key: string]: boolean }): string[] {
    return Object.keys(obj).filter((key) => obj[key] === true);
  }

  onLogout() {
    this.authService.logout();
    const Toast = Swal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 1000,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener('mouseenter', Swal.stopTimer);
        toast.addEventListener('mouseleave', Swal.resumeTimer);
      },
    });

    Toast.fire({
      icon: 'success',
      title: 'Loged Out',
    });
    this.router.navigate(['/login']);
  }
}
