import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  constructor(private router: Router, private authService: AuthService) {}
  loginForm: FormGroup;

  ngOnInit(): void {
    localStorage.setItem('is', JSON.stringify(this.authService.isAuthenticatedUser()));
    this.loginForm = new FormGroup({
      email: new FormControl(null, [Validators.required, Validators.email]),
      password: new FormControl(null, Validators.required),
    });
  }

  onLogin() { 
    if (this.authService.login(this.loginForm.value)) {
      // console.log(this.loginForm.value.email);
      // console.log(this.loginForm.value.password);
      this.router.navigate(['/userList']);
      // ***************Alert Box****************
      const Toast = Swal.mixin({
        toast: true,
        position: 'top-end',
        showConfirmButton: false,
        timer: 1500,
        timerProgressBar: true,
        didOpen: (toast) => {
          toast.addEventListener('mouseenter', Swal.stopTimer);
          toast.addEventListener('mouseleave', Swal.resumeTimer);
        },
      });
      Toast.fire({
        icon: 'success',
        title: 'Signed in successfully',
      });
    } else {
      const Toast = Swal.mixin({
        toast: true,
        position: 'top',
        showConfirmButton: false,
        timer: 1000,
        timerProgressBar: true,
        didOpen: (toast) => {
          toast.addEventListener('mouseenter', Swal.stopTimer);
          toast.addEventListener('mouseleave', Swal.resumeTimer);
        },
      });
      Toast.fire({
        icon: 'warning',
        iconColor: 'red',
        title: 'Invalid Credentials',
      });
      console.log('Not valid');
    }
  }
}
<div class="container">
  <div class="row">
    <div>
      <form [formGroup]="loginForm" (ngSubmit)="onLogin()">
        <h2>Login</h2>
        <div id="email-group">
          <input type="text" id="email" name="email" formControlName="email" placeholder="Email"><br>
          <p [ngClass]="!loginForm.get('email').valid && loginForm.get('email').touched ? 'error_display' : 'error_hiden'" >Invalid Email!</p>
        </div>
        <div id="passsword-group">
          <input type="password" id="password" name="password" formControlName="password" placeholder="Password"><br>
          <p class="error" [ngClass]="!loginForm.get('password').valid && loginForm.get('password').touched ? 'error_display' : 'error_hiden' ">Password is Required!</p>
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  </div>
</div>
