import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent  implements OnInit{

  constructor(private router : Router){}
    loginForm: FormGroup;
    emailId = 'rishit@gmail.com';
    password = '123'
  ngOnInit(): void {
    this.loginForm = new FormGroup({
      'email': new FormControl(null, [Validators.required,Validators.email]),
      'password': new FormControl(null , Validators.required),
    })
  }

  onLogin() {

    if (this.loginForm.value.email === this.emailId && this.loginForm.value.password === this.password  ) {
      console.log(this.loginForm.value.email);
      console.log(this.loginForm.value.password);
      this.router.navigate(['/userForm'])
    }
    else {
      alert("Please Enter valid Inputs")
    }
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">
      <form [formGroup]="loginForm" (ngSubmit)="onLogin()">
        <h2>Login Form</h2>
        <div id="email-group">
          <!-- <label for="email">Email</label><br> -->
          <input type="text" id="email" name="email" formControlName="email" placeholder="Email"><br>
          <p [ngClass]="!loginForm.get('email').valid && loginForm.get('email').touched ? 'error_display' : 'error_hiden'" >Invalid Email!</p>
        </div>
        <div id="passsword-group">
          <!-- <label for="password">Password</label><br> -->
          <input type="text" id="password" name="password" formControlName="password" placeholder="Password"><br>
          <p class="error" [ngClass]="!loginForm.get('password').valid && loginForm.get('password').touched ? 'error_display' : 'error_hiden' ">Password is Required!</p>
        </div>
        <button type="submit" [disabled]="!loginForm.valid">Login</button>
      </form>
    </div>
  </div>
</div>
