import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'Task_2';

   loginForm!: FormGroup;



  ngOnInit(): void {
    this.loginForm = new FormGroup({
      'email': new FormControl(null, [Validators.required,Validators.email]),
      'password': new FormControl(null , Validators.required),
    })
  }

  onLogin() {
    console.log(this.loginForm.value);

  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">
      <form [formGroup]="loginForm" (ngSubmit)="onLogin()">
        h2
        <div id="email-group">
          <label for="email">Enter Email</label><br>
          <input type="text" id="email" name="email" formControlName="email"><br>
          <p class="error" *ngIf="!loginForm.get('email')?.valid && loginForm.get('email')?.touched">Invalid Email!</p>
        </div>
        <div id="passsword-group">
          <label for="password">Enter Password</label><br>
          <input type="text" id="password" name="password" formControlName="password"><br>
          <p class="error" *ngIf="!loginForm.get('password')?.valid && loginForm.get('password')?.touched">Password is Required!</p>
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  </div>
</div>
