import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DataService } from '../data.service';
import {  Router } from '@angular/router';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.scss']
})
export class UserFormComponent implements OnInit{
  userDataForm: FormGroup;
  qualification = '';
  editedData: any;
  hobbyList = [
  { id: 1, label: 'cricket' },
  { id: 2, label: 'music' },
  { id: 3, label: 'travelling' },
  { id: 4, label: 'finance' },
  { id: 5, label: 'others' },

];

  constructor(private formBuilder: FormBuilder,private dataService: DataService, private router : Router){}


  ngOnInit(): void {

      this.userDataForm = this.formBuilder.group({
      'name' : new FormControl(null, Validators.required),
      'dob' : new FormControl(null, Validators.required),
      'email' : new FormControl(null, [Validators.required,Validators.email]),
      'number' : new FormControl(null, Validators.required),
      'schoolName': new FormControl(null, Validators.required),
      'qualification': new FormControl(null, Validators.required),
      'percentage': new FormControl(null, Validators.required),
      //  'hobby': new FormGroup({
      //   'cricket' : new FormControl(null),
      //   'travelling' : new FormControl(null),
      //   'music' : new FormControl(null),
      //   'finance' : new FormControl(null),
      //   'others' : new FormControl(null),
      // }),
      'hobby': this.formBuilder.array([]),
      'gender' : new FormControl(null, Validators.required),
      'address' : new FormControl(null),
      'summery' : new FormControl(null)
     })

    if(this.dataService.editMode) {
      this.editedData = this.dataService.getSharedData();
      this.userDataForm.setValue(this.editedData)

     }
  }
 u
    userDataForm.valueChanges.subscribe((value) => {
      const selectedValues = Object.keys(value.hobby)
        .filter((key) => value.hobby[key])
        .map((key) => +key);

      console.log('Selected Checkbox Values: ', selectedValues);
    });


  changeQua(e:any) {
    console.log(e.target.value);
    this.qualification = e.target.value
     console.log(this.userDataForm.value.hobby);
  }

  onSubmit(data: any) {
    if (!this.userDataForm.valid) {
      alert("Please enter Valid Data")
    }
    else {
      console.log(this.userDataForm.value);
      // console.log(this.userDataForm.value);
      this.dataService.setSharedData(this.userDataForm.value);
      Swal.fire({
        position: 'center',
        icon: 'success',
        title: 'Data Saved',
        showConfirmButton: false,
        timer: 800
      })
      this.router.navigate(['/details'])
      this.dataService.editMode = false;
    }

  }
}
