import { Component } from '@angular/core';

@Component({
  selector: 'app-user-data',
  templateUrl: './user-data.component.html',
  styleUrls: ['./user-data.component.scss']
})
export class UserDataComponent {

  courseList: course[];

  ngOnInit() {
    this.getCourse()
  }

  getCourse() {
    this.courseList = [
      {id:1,name:'abc',isselected:false},
      {id:2,name:'xxx',isselected:false},
      {id:3,name:'YYY',isselected:false},
    ]
  }
  onChange() {
    console.log(this.courseList);

  }
}
class course {
    id: number;
    name: string;
    isselected:boolean
  }
